import nullimg from './city-220190_1920.jpg'
const MovieDesc = ({id})=>
{
    
    if(id.show.image!==null)
        {
            var im = id.show.image.original
        }
        else{
            var im = nullimg
        }

        function a(id)
        {
            console.log(id)
            return <>{id.show.summary}</>
        }

    return(
        <div className="des">
            <h1>{id.show.name}</h1>
            <img src={im} style={{width:"300px"}}/>
            <div className="para">
                {a()}
            </div>
        </div>
    );
}
export default MovieDesc;
